import React, { Component, useState } from "react";
import { TodoData } from "./TodoData";
import '../App.css';

function Sidebar ({ childToParent }) {  
    this.state = {
        active: false,
    };
    return (
        <div>
            <h1>TODO List</h1>
            <div className="Sidebar">
                <ul class="SidebarList">
                    {TodoData.map((val, key) => {
                        return <li className={this.state.active ? 'active-row': 'row'}  key={key} onClick={() => childToParent(val.additionalDetails)}>
                            {val.heading}
                        </li>
                    })}
                </ul>
            </div>
        </div>
    );
}

export default Sidebar;
