import React, { Component, useState } from "react";
import { TodoData } from "./TodoData";
import '../App.css';

function Sidebar({ displayActiveElement }) {

    const [todoItems, setItems] = useState(TodoData);
    const [activeKey, setActiveKey] = useState("");

    function handleClick(key, additionalDetails) {
        displayActiveElement(additionalDetails);
        setActiveKey(key);
    }

    const removeToDoItem = (id) => {
        const updatedList = todoItems.filter((item) => item.id !== id);
        setItems(updatedList);
    };

    return (
        <div>         
            <div className="Sidebar">
                <ul class="SidebarList">
                    {todoItems.map((val, key) => {
                        return <li className={activeKey === key ? 'row active' : 'row'} key={key} onClick={() => handleClick(key, val.additionalDetails)}>
                            {val.heading}
                            <button
                                className='delete-button'
                                onClick={() => removeToDoItem(val.id)}>
                             X
                            </button>
                        </li>
                    })}
                </ul>
            </div>
        </div>
    );
}

export default Sidebar;
