import React, { Component, useState } from "react";
import { TodoData } from "./TodoData";
import '../App.css';

function Sidebar ({ childToParent }) {   

  const [items, setItems] = {TodoData};

      
  const [activeKey, setActiveKey] = useState("");

  function handleClick(key, additionalDetails){
    childToParent(additionalDetails);
    setActiveKey(key);
  }

  const removeItem = (id) => {
    const updatedList = items.filter((item) => item.id !== id);
    setItems(updatedList);
  };

//   const toggleClass = () => {
//     setActive(!isActive);
//   };
    return (
        <div>
            <h1>TODO List</h1>
            <div className="Sidebar">
                <ul class="SidebarList">
                    {items.map((val, key) => {
                        return <li className={activeKey === key ? 'row active': 'row'}  key={key} onClick={() => handleClick(key, val.additionalDetails) }>
                            {val.heading}
                            <button
              className='delete-button'
              onClick={() => removeItem(val.id)}
            >
              X
            </button>
                        </li>
                    })}
                </ul>
            </div>
        </div>
    );
}

export default Sidebar;
