import React, { useState } from "react";
import { TodoData } from "./TodoData";
import '../App.css';

function Sidebar({ displayActiveElement }) {
    const [todoItems, setItems] = useState(TodoData);
    const [activeId, setActiveId] = useState("");

    function handleClick(val) {
        displayActiveElement(val.additionalDetails);
        setActiveId(val.id);
    }

    // function removeToDoItem = (id) => {
    //     const updatedList = todoItems.filter((item) => item.id !== id);
    //     setItems(updatedList);  
    //     displayActiveElement("");     
    // };

    function removeToDoItem(id) {
        const updatedList = todoItems.filter((item) => item.id !== id);
        setItems(updatedList);  
        displayActiveElement(""); 
    }

    return (
        <div>
            <div className="Sidebar">
                <ul class="SidebarList">
                    {todoItems.map((item, key) => {
                        return <li className={activeId === item.id ? 'row active' : 'row'} key={key} onClick={() => handleClick(item)}>
                            {item.heading}
                            <button
                                className='delete-button'
                                onClick={() => removeToDoItem(item.id)}>
                                X
                            </button>
                        </li>
                    })}
                </ul>
            </div>
        </div>
    );
}

export default Sidebar;
