import React from 'react';

class TodoList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      entries: []
    };

    this.handleItem = this.handleItem.bind(this);
    this.handleTrash = this.handleTrash.bind(this);
    this.handleAdd = this.handleAdd.bind(this);
  }

  renderListItem(e) {
    if(e.struck) {
      return (
        <ListItem
          key={e.key}
          value={<strike>{e.item}</strike>}
          handleTrash={() => this.handleTrash(e)}
          handleItem={() => this.handleItem(e)}
        />
      );
    }

    else {
     return(
      <ListItem
          key={e.key}
          value={e.item}
          handleTrash={() => this.handleTrash(e)}
          handleItem={() => this.handleItem(e)}
        />
      );
    }
  }

  handleAdd(e) {
    const entries = this.state.entries;
    entries.push({item: e, struck: false, key: entries.length});
    this.setState({
      entries: entries
    });
  }

  handleTrash(e) {
    const entries = this.state.entries;
    for(let i = 0; i < entries.length; i++) {
      if(entries[i] === e) {
        entries.splice(i, 1);
      }
    }

    this.setState({
      entries: entries
    });
  }

  handleItem(e) {  
    const entries = this.state.entries;
    let index = entries.findIndex(ele => e.key === ele.key);
    entries[index].struck = !entries[index].struck;
    this.setState({
      entries: entries
    });
  }

  render() {
    return (
      <div className="Sidebar">
      <ul class="SidebarList">
          {TodoData.map((val, key) => {
              return <li className="row" key={key}>
                  {val.heading} {isActive}
              </li>;
          })};
      </ul>
    </div>
    );
  }
}

export default TodoList;
